begin
wwv_flow_api.create_plugin(
 p_id=>wwv_flow_api.id(64099344025230736436)
,p_plugin_type=>'DYNAMIC ACTION'
,p_name=>'RW.APEX.DYNAMIC.SELECT'
,p_display_name=>'APEX Dynamic Selection'
,p_category=>'EXECUTE'
,p_supported_ui_types=>'DESKTOP:JQM_SMARTPHONE'
,p_plsql_code=>wwv_flow_string.join(wwv_flow_t_varchar2(
'FUNCTION F_RENDER (',
'    P_DYNAMIC_ACTION   IN   APEX_PLUGIN.T_DYNAMIC_ACTION,',
'    P_PLUGIN           IN   APEX_PLUGIN.T_PLUGIN',
') RETURN APEX_PLUGIN.T_DYNAMIC_ACTION_RENDER_RESULT AS',
'',
'    L_RESULT          APEX_PLUGIN.T_DYNAMIC_ACTION_RENDER_RESULT;',
'    VR_ITEM_NAME      CONSTANT P_DYNAMIC_ACTION.ATTRIBUTE_01%TYPE := P_DYNAMIC_ACTION.ATTRIBUTE_01;',
'    VR_ICON_UNSEL     CONSTANT P_DYNAMIC_ACTION.ATTRIBUTE_02%TYPE := P_DYNAMIC_ACTION.ATTRIBUTE_02;',
'    VR_ICON_SEL       CONSTANT P_DYNAMIC_ACTION.ATTRIBUTE_03%TYPE := P_DYNAMIC_ACTION.ATTRIBUTE_03;',
'    VR_TEXT_UNSEL_ALL CONSTANT P_DYNAMIC_ACTION.ATTRIBUTE_04%TYPE := P_DYNAMIC_ACTION.ATTRIBUTE_04;',
'    VR_TEXT_SEL_ALL   CONSTANT P_DYNAMIC_ACTION.ATTRIBUTE_05%TYPE := P_DYNAMIC_ACTION.ATTRIBUTE_05;',
'    VR_USE_TYPE       CONSTANT P_DYNAMIC_ACTION.ATTRIBUTE_06%TYPE := P_DYNAMIC_ACTION.ATTRIBUTE_06;',
'    VR_LIST_SEPERATOR CONSTANT VARCHAR2(1 CHAR) := '':'';',
'    VR_REGEX CONSTANT VARCHAR2(20 CHAR) := ''\{\{([^}]+)\}\}'';',
'BEGIN',
'',
'    APEX_JAVASCRIPT.ADD_LIBRARY(',
'        P_NAME                   => ''script'',',
'        P_CHECK_TO_ADD_MINIFIED  => TRUE,',
'        P_DIRECTORY              => P_PLUGIN.FILE_PREFIX,',
'        P_KEY                    => ''adynseljssrc''',
'    );',
'',
'    L_RESULT.JAVASCRIPT_FUNCTION := ''function () { dynamicSelection.initialize(this,'' ||',
'        APEX_JAVASCRIPT.ADD_VALUE(VR_USE_TYPE,TRUE) ||',
'        APEX_JAVASCRIPT.ADD_VALUE(VR_REGEX,TRUE) ||',
'        APEX_JAVASCRIPT.ADD_VALUE(VR_ITEM_NAME,TRUE) ||',
'        APEX_JAVASCRIPT.ADD_VALUE(VR_LIST_SEPERATOR,TRUE) ||',
'        APEX_JAVASCRIPT.ADD_VALUE(VR_ICON_UNSEL,TRUE) ||',
'        APEX_JAVASCRIPT.ADD_VALUE(VR_ICON_SEL,TRUE) ||',
'        APEX_JAVASCRIPT.ADD_VALUE(VR_TEXT_UNSEL_ALL,TRUE) ||',
'        APEX_JAVASCRIPT.ADD_VALUE(VR_TEXT_SEL_ALL,FALSE) ||',
'    ''); }'';',
'',
'    RETURN L_RESULT;',
'END;'))
,p_api_version=>1
,p_render_function=>'F_RENDER'
,p_standard_attributes=>'REGION:JQUERY_SELECTOR:REQUIRED'
,p_substitute_attributes=>true
,p_subscribe_plugin_settings=>true
,p_help_text=>wwv_flow_string.join(wwv_flow_t_varchar2(
'<p>This plug-in is used to create a dynamic selection list to every report and region in APEX. Just load this item on page load.</p>',
'<p>if you use the plug-in in a report then you need to add a column with these two {{#YOUR_SELECT_VALUE#}} to the report as you can see in the example below:</p>',
'<pre>',
'SELECT',
'    /* the plug-in will search for all element in the {{ }} and will replace this with the select item',
'    when you click on the item then selection will be stored to set item in plug-in attributes',
'    if you want to use the plug-in for non reports or list then add just element with class="dynamic-selection-element" */',
'    ''{{'' || ROWNUM || ''}}'' AS SELECT_ITEMS,',
'    ROWNUM AS AS DUMMY_VALUE_COLUMN,',
'    SYSDATE - ROWNUM AS AS DUMMY_VALUE_COLUMN_02',
'FROM',
'    DUAL',
'CONNECT BY',
'    ROWNUM <= 10',
'</pre>',
'<p>The plug-in just searches for the cells where the values are in the two curly braces <b>{{ }}</b> and replaces them with the selection item. If you select one then this value is stored into set item</p>',
'<p>The plug-in works with classic report, list, ig and ir</p>',
'<p>If you want to use it in lists or static regions then you have to add there a element e.g. <div class="dynamic-selection-element">{{Value1}}</div> then the plug-in also finds your elements so you are free to use it everywhere and not only in repor'
||'ts.</p>'))
,p_version_identifier=>'1.0'
,p_about_url=>'https://github.com/RonnyWeiss/APEX-Dynamic-Selection'
,p_files_version=>1464
);
